import axios from "axios";

const API_BASE = "https://nus-iot-function.azurewebsites.net/api";

export async function startCapture() {
  return (await axios.post(`${API_BASE}/start`)).data;
}

export async function stopCapture() {
  return (await axios.post(`${API_BASE}/stop`)).data;
}

export async function getEngagement() {
  return (await axios.get(`${API_BASE}/engagement`)).data;
}

export async function enrollStudent(name, fileOrBlob) {
  const formData = new FormData();
  formData.append("student_name", name);
  formData.append("file", fileOrBlob);

  const res = await axios.post("https://nus-iot-function.azurewebsites.net/api/enroll-student", formData);
  return res.data;
}
